import React, { Component } from "react";
import './components-css/Main.css';

import { FaPlus } from 'react-icons/fa' // form
import { FaEdit, FaWindowClose } from 'react-icons/fa' // ul

export default class Main extends Component {
  state = {
    novaTarefa: '',
    tarefas: [],
    index: -1
  }

  handleDelete = (e, index) => {
    const { tarefas } = this.state
    const novasTarefas = [...tarefas];
    novasTarefas.splice(index, 1);

    this.setState({
        tarefas: [...novasTarefas]
    })
  };

  handleEdit = (e, index) => {
    const { tarefas } = this.state;
    this.setState({
      index,
      novaTarefa: tarefas[index]
    })

  }

  handleSubmit = (e) => {
    e.preventDefault();
    const { tarefas, index } = this.state;
    let { novaTarefa } = this.state;
    novaTarefa = novaTarefa.trim();

    if(tarefas.indexOf(novaTarefa) !== -1) return;
    const news = [...tarefas];

    if(index === -1){
      this.setState({
        tarefas: [...news, novaTarefa],
        novaTarefa: ''
      })
    }else{

    }

  }


  handleChange = (e) => {
    this.setState({
      novaTarefa: e.target.value,
    });
  }

  render() {
    const { novaTarefa, tarefas } = this.state
    return (
      <div className="div-main">
        <h1>Lista de tarefas</h1>

        <form onSubmit={this.handleSubmit} >
          <input value={novaTarefa} onChange={this.handleChange} type="text"></input>
          <button type="submit"><FaPlus/></button>
        </form>

        <ul className="tarefas">
          {tarefas.map((tarefas, index) => (
            <li key={tarefas.id}>
              {tarefas}
              <span className="buttons">
                <FaWindowClose onClick={(e) => {this.handleDelete(e, index)}} className="closeButton"/>
                <FaEdit onClick={(e) => {this.handleEdit(e, index)}} className="editButton"/>
              </span>
              </li>
          ))}
        </ul>

      </div>
    );
  }
};

