import React, { Component } from "react";
import './components-css/Main.css';

import { FaPlus } from 'react-icons/fa' // form
import { FaEdit, FaWindowClose } from 'react-icons/fa' // ul

export default class Main extends Component {
  state = {
    novaTarefa: '',
    tarefas: [

    ]
  }

  handleDelete = (e, index) => {
    console.log('deletando')
  };

  handleEdit = (e, index) => {
    console.log('editando', index)
  }

  handleSubmit = (e) => {
    e.preventDefault();
    const { tarefas } = this.state;
    let { novaTarefa } = this.state;
    novaTarefa = novaTarefa.trim();

    if(tarefas.indexOf(novaTarefa) !== -1) return;
    const news = [...tarefas];

    this.setState({
      tarefas: [...news, novaTarefa]
    })
  }


  handleChange = (e) => {
    this.setState({
      novaTarefa: e.target.value,
    });
  }

  render() {
    const { novaTarefa, tarefas } = this.state
    return (
      <div className="div-main">
        <h1>Lista de tarefas</h1>

        <form onSubmit={this.handleSubmit} >
          <input value={novaTarefa} onChange={this.handleChange} type="text"></input>
          <button type="submit"><FaPlus/></button>
        </form>

        <ul className="tarefas">
          {tarefas.map(tarefas => (
            <li key={tarefas.id}>
              {tarefas}
              <span className="buttons">
                <FaWindowClose onClick={this.handleDelete} className="closeButton"/>
                <FaEdit onClick={this.handleEdit} className="editButton"/>
              </span>
              </li>
          ))}
        </ul>

      </div>
    );
  }
};

